import React from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useState, useEffect } from "react";

function Bookingpage({match}) {
  const [loading, setloading] = useState(true);
  const [error, seterror] = useState();
  const [room, setroom] = useState();

//   let { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setloading(true);
        const data = (
          await axios.post("/api/rooms/getroombyid", { roomid: match.params.roomid })
        ).data;

        setroom(data);
        setloading(false);
        console.log(data);
      } catch (error) {
        seterror(true);
        console.log(error);
        setloading(false);
      }
    };
    fetchData();
  }, []);

  return (
    <div>
      {loading ? (
        <h1>Loading...</h1>
      ) : error ? (
        <h1>Error</h1>
      ) : (
        <div>
          <div className="row">
            <div className="col-md-5">
            <h1>{room.name}</h1></div>
            <div className="col-md-5"><img src={room.imageurls[0]} height="200px" width="100%" alt={room.name}/></div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Bookingpage;
